import React from 'react';
import { Route, Routes, Outlet, Link, useNavigate, useLocation, useParams } from 'react-router-dom';
import ClassManagement from './ClassManagement';
import StudentManagement from './StudentManagement';
import PaymentManagement from './PaymentManagement';
// 다른 컴포넌트들도 import

// Dashboard 컴포넌트 추가
const Dashboard = () => {
  return <div>여기는 대시보드입니다.</div>;
};

const Admin = () => {
  let navigate = useNavigate();
  let location = useLocation();
  let { subpath } = useParams();

  const url = location.pathname;

  return (
    <div>
      <h1>Admin 페이지</h1>
      <div className="admin-menu">
        <Link to={`${url}/class-management`}>클래스관리</Link>
        <Link to={`${url}/student-management`}>학생관리</Link>
        <Link to={`${url}/payment-management`}>결제관리</Link>
        {/* ... 다른 메뉴들 */}
      </div>

      <Routes>
        <Route path="/" element={<Dashboard />} />
        <Route path="class-management" element={<ClassManagement />} />
        <Route path="student-management" element={<StudentManagement />} />
        <Route path="payment-management" element={<PaymentManagement />} />
        {/* ... 다른 라우트들 */}
      </Routes>
    </div>
  );
};

export default Admin;
