import React, { useState } from 'react';
import styles from '../style/signup.module.css';


const countryCodes = [
    { callingCode: '+1', name: 'United States' },
    { callingCode: '+82', name: 'South Korea' },
    // 여기에 더 추가 가능
  ];
  
  const timezones = [
    ['UTC-11:00', 'UTC-10:00', 'UTC-09:30'],
    ['UTC-09:00', 'UTC-08:30', 'UTC-08:00'],
    // 여기에 더 추가 가능
  ];

const SignupForm = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    birthYear: '',
    birthMonth: '',
    birthDay: '',
    country_code: '',
    phone_number: '',
    Nationality: '',
    country: '',
    currentCity: '',
    timezones: [],
    termsCheckbox: false,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // 서버로 데이터 전송하는 로직
  };
 return (
    <form action="/signup" method="post" className="signup-form" onSubmit={handleSubmit}>
      {



      }
      
      <div className="form-group">
        <label htmlFor="Nationality">Nationality:</label>
        <input
          list="nationalities"
          id="Nationality"
          name="Nationality"
          className="form-control"
          style={{ width: '100%' }}
          required=""
          placeholder="-select-"
          onChange={handleChange}
        />
        {/* 여기에 datalist */}
      </div>
      
      <div className="form-group">
        <label htmlFor="country">Language you use:</label>
        <input
          list="languages"
          id="country"
          name="country"
          className="form-control"
          style={{ width: '100%' }}
          required=""
          placeholder="Up to 3 languages"
          onChange={handleChange}
        />
        {/* 여기에 datalist */}
      </div>

      <div className="form-group">
        <label htmlFor="currentCity">Please indicate your country of residence with states and city:</label>
        <input
          type="text"
          id="currentCity"
          name="currentCity"
          className="form-control"
          placeholder="country / state / city"
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label>Please choose your available class hours in your local time:</label>
        <div className="timezone-container">
          {timezones.map((row, rowIndex) => (
            <div key={rowIndex} className="timezone-row">
              {row.map((timezone) => (
                <div key={timezone} className="timezone-item">
                  <input
                    type="checkbox"
                    id={timezone}
                    name="timezones"
                    value={timezone}
                  />
                  <label htmlFor={timezone}>{timezone}</label>
                </div>
              ))}
            </div>
          ))}
        </div>
      </div>

      <input type="checkbox" id="termsCheckbox" disabled />
      <label htmlFor="termsCheckbox">I agree to the terms and conditions</label>

      <button type="submit" id="submitButton">
        Submit application
      </button>
    </form>
  );
};
export default SignupForm;
