import React, { useState, useEffect } from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import axios from 'axios';

const ZoomMeetingBeforeModal = ({ show, handleClose, selectedStudent }) => {
  const [zoomLink, setZoomLink] = useState('');

  useEffect(() => {
    // Zoom 미팅 정보를 불러오는 로직
    if (selectedStudent) {
      const fetchZoomLink = async () => {
        try {
            const response = await axios.get(`${API_BASE_URL}/getZoomMeetingLink/${tno}`);
        } catch (error) {
          console.error('Zoom 정보를 불러오는 데 실패했습니다.', error);
        }
      };

      fetchZoomLink();
    }
  }, [selectedStudent]);

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>줌 미팅 전 상태</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <h5>학생 정보</h5>
        <p>이름: {selectedStudent ? selectedStudent.name : ''}</p>
        <p>줌 미팅 링크: {zoomLink}</p>
        {/* 필요한 정보를 더 표시할 수 있음 */}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          닫기
        </Button>
        <Button variant="primary">
          저장
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default ZoomMeetingBeforeModal;
