import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const TeacherManagement = () => {
  const [showModal, setShowModal] = useState(false);

  const toggleModal = () => {
    setShowModal(!showModal);
  };

  return (
    <div className="container">
      <h1 className="mt-3 mb-3">강사 관리</h1>
      <div className="mb-3 d-flex justify-content-between">
        <div>
          <button className="btn btn-primary">재직 강사</button>
          <button className="btn btn-secondary ml-2">해직 강사</button>
          <button className="btn btn-secondary ml-2">전체 강사</button>
        </div>
        <button className="btn btn-success" onClick={toggleModal}>강사 생성</button>
      </div>
      {/* 모달 창 */}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={toggleModal}>&times;</span>
            {/* 여기에 강사 정보 입력 폼이 들어갈 공간 */}
          </div>
        </div>
      )}
     
      <table className="table">
        <thead>
          <tr>
            <th>강사번호</th>
            <th>이름</th>
            <th>이메일</th>
            <th>전화번호</th>
            <th>상세 정보</th>
            <th>강의 보기</th>
            <th>추가 수업 가능 시간</th>

            강사번호 이름 이메일 전화번호 상세정보 강의보기 추가수업가능시간 수당관리
          </tr>
        </thead>
        <tbody>
          {/* 여기에 강사 정보를 루프로 돌려서 넣으면 돼 */}
        </tbody>
      </table>
      <button className="btn btn-info">더 보기</button>
    </div>
  );
};

export default TeacherManagement;
