import React, { useState ,useEffect   } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const classTypes = ["온라인", "오프라인", "혼합"];

const ClassManagement = () => {
  const [errorMessage, setErrorMessage] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [teachers, setTeachers] = useState([]);  
  const [accountNumber, setAccountNumber] = useState('');
  const [zoomMeetingLink, setZoomMeetingLink] = useState('');
  const [classCategory, setClassCategory] = useState('');
  const [teacher, setTeacher] = useState('');
  const [classRoom,setClassRoom] = useState([]);
  const [textbook, setTextbook] = useState('');
  const [classLevels, setClassLevels] = useState(['', '', '']);
  const [weeklyFrequency, setWeeklyFrequency] = useState(0);
  const [classTime, setClassTime] = useState('');
  const [scheduleDays, setScheduleDays] = useState([]);
  const [timezones, setTimezones] = useState([]);
  const [selectedTimes, setSelectedTimes] = useState([]);
  const [zoomMeetingID, setZoomMeetingID] = useState("");
  const [zoomPassword, setZoomPassword] = useState("");
  const [selectedLevels, setSelectedLevels] = useState([]);
  const levels = ['초급', '중급', '고급'];


  const handleLevelClick = (level) => {
    if (selectedLevels.includes(level)) {
      setSelectedLevels(selectedLevels.filter(l => l !== level));
    } else {
      setSelectedLevels([...selectedLevels, level]);
    }
  };

  const removeTime = (time) => {
    setSelectedTimes(selectedTimes.filter((t) => t !== time));
  };
  const handleDoubleClick = (time) => {
    // 중복 선택 방지
    if (!selectedTimes.includes(time)) {
      setSelectedTimes([...selectedTimes, time]);
    }
  };

  const toggleModal = () => {
    setShowModal(!showModal);
  };
  
  useEffect(() => {
    const fetchTeachers = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/ClassLoad`); 
        setClassRoom(response.data);
      } catch (error) {
        console.error('클래스 정보 로딩에 실패했습니다.', error);
      }
    };

    fetchTeachers();
  }, []); // 빈 dependency array를 넣어서 컴포넌트가 마운트 될 때만 실행되도록 함
  
  useEffect(() => {
    if (showModal) {
      axios.get(`${API_BASE_URL}/getTimezones`)
        .then(response => {
          setTimezones(response.data);
        })
        .catch(error => {
          console.error('수업시간 데이터를 불러오는 데 실패했습니다', error);
        });
    }
  }, [showModal]);
  
  useEffect(() => {
    if (showModal) {
      fetchTimezones();
      axios.get(`${API_BASE_URL}/teacherLoad`)
        .then(response => {
          setTeachers(response.data);
        })
        .catch(error => {
          console.error('강사 데이터를 불러오는 데 실패했습니다', error);
        });
    }
  }, [showModal]); 
  
  const handleSubmit = async () => {
    try {
      const formData = {
        classCategory,
        teacher,
        textbook,
        weeklyFrequency,
        selectedTimes,
        zoomMeetingLink,
        zoomMeetingID,
        zoomPassword,
      };
  
      const response = await axios.post(`${API_BASE_URL}/ClassCreate`, JSON.stringify(formData), {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.status === 201) {
        console.log('클래스가 성공적으로 생성되었습니다.');
        toggleModal(); 
      }
  
      toggleModal(); // 모달 닫기
    } catch (error) {
      console.error('클래스 생성에 실패했습니다.', error);
      console.log(API_BASE_URL);
    }
  };
  async function fetchTimezones() {
    try {
      const response = await axios.get(`${API_BASE_URL}/timezones`);
      setTimezones(response.data);
    } catch (error) {
      console.error("API 호출 중 에러 발생:", error);
    }
  }
  const handleDayClick = (day) => {
    if (selectedDays.includes(day)) {
      setSelectedDays(selectedDays.filter(d => d !== day));
    } else {
      setSelectedDays([...selectedDays, day]);
    }
  };

    return (
      <div className="container">
        <h1 className="mt-3 mb-3">클래스 관리</h1>
        <div className="mb-3 d-flex justify-content-between">
          <div>
            <button className="btn btn-primary">전체 클래스</button>
            <button className="btn btn-secondary ml-2">활동 클래스</button>
            <button className="btn btn-secondary ml-2">종료 클래스</button>
          </div>
          <button className="btn btn-success" onClick={toggleModal}>클래스 생성</button>
        </div>
       
        {showModal && (
          <div className="modal fade show d-block" tabIndex="-1">
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">클래스 생성</h5>
                  <button type="button" className="btn-close" onClick={toggleModal}></button>
                </div>
                <div className="modal-body">
                  

          
        <div className="row mb-3">
            <label className="col-sm-3 col-form-label">강의 타입</label>
            <div className="col-sm-4">
            <select className="form-control" id="classType" onChange={e => setClassCategory(e.target.value)}>
              {classTypes.map((type, index) => (
                <option key={index} value={type}>
                  {type}
                </option>
              ))}
            </select>
            </div>
          </div>

          <div className="row mb-3">
            <label className="col-sm-3 col-form-label">강사</label>
            <div className="col-sm-4">
            <select className="form-control" id="isFreeClassteacher" onChange={e => {
              setTeacher(e.target.value);
            }}>
              {teachers.map((teacher, index) => (
                <option key={index} value={teacher.tno}>
                  {teacher.username}
                </option>
              ))}
            </select>
            </div>
          </div>

          <div className="row mb-3">
            <label className="col-sm-3 col-form-label">교재</label>
            <div className="col-sm-4">
              <input type="text" placeholder="" value={textbook} onChange={(e) => setTextbook(e.target.value)} className="form-control" />
            </div>
          </div>

         <div className="row mb-3">
            <label className="col-sm-3 col-form-label">클래스 레벨</label>
            <div className="col-sm-9">
              {levels.map((level, index) => (
                <div key={index} style={{ display: 'inline-block', margin: '0 10px' }}>
                  <input type="checkbox" id={level} checked={selectedLevels.includes(level)} onChange={() => handleLevelClick(level)} />
                  <label htmlFor={level}>{level}</label>
                </div>
              ))}
            </div>
          </div>
          <div className="row mb-3">
            <label className="col-sm-3 col-form-label">선택된 레벨</label>
            <div className="col-sm-9">
              {selectedLevels.join(", ")}
            </div>
          </div>
          <div className="row mb-3">
            <label className="col-sm-3 col-form-label">주 횟수</label>
            <div className="col-sm-4">
              <input type="number" placeholder="" value={weeklyFrequency} onChange={(e) => setWeeklyFrequency(e.target.value)} className="form-control" />
            </div>
          </div>

          <div className="row mb-3">
              <label className="col-sm-3 col-form-label">진행 요일</label>
              <div className="col-sm-9">
                {daysOfWeek.map((day, index) => (
                  <div key={index} style={{ display: 'inline-block', margin: '0 10px' }}>
                    <input type="checkbox" id={day} checked={selectedDays.includes(day)} onChange={() => handleDayClick(day)} />
                    <label htmlFor={day}>{day}</label>
                  </div>
                ))}
              </div>
            </div>

            <div className="row mb-3">
              <label className="col-sm-3 col-form-label">선택된 요일</label>
              <div className="col-sm-9">
                {selectedDays.join(", ")}
              </div>
            </div>
          
  
          <div className="row mb-3">
            <label className="col-sm-3 col-form-label">수업시간</label>
            <div className="col-sm-9">
              {/* 기존의 선택 가능한 시간 목록 */}
              <div className="col-sm-4">
                <select multiple className="form-control" onChange={e => {
                  const selectedOptions = Array.from(e.target.selectedOptions).map(option => option.value);
                  setClassTime(selectedOptions);
                }}>
                  {timezones && timezones.flat().map((time, index) => (
                    <option key={index} value={time} onDoubleClick={() => handleDoubleClick(time)}>
                      {time}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>

          <div className="row mb-3">
            <label className="col-sm-3 col-form-label">선택된 시간</label>
            <div className="col-sm-9">
              <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                {selectedTimes.map((time, index) => (
                  <div key={index} style={{ margin: '0 10px' }}>
                    {time}
                    <button onClick={() => removeTime(time)}>x</button>
                  </div>
                ))}
              </div>
            </div>
          </div>
          <div className="row mb-3">
            <label className="col-sm-3 col-form-label">줌 미팅 링크</label>
            <div className="col-sm-4">
              <input type="text" placeholder="" value={zoomMeetingLink} onChange={(e) => setZoomMeetingLink(e.target.value)} className="form-control" />
            </div>
          </div>

          <div className="row mb-3">
          <label className="col-sm-3 col-form-label">줌 미팅 아이디</label>
          <div className="col-sm-4">
            <input type="text" value={zoomMeetingID} onChange={(e) => setZoomMeetingID(e.target.value)} className="form-control" />
          </div>
        </div>

        {/* 줌 미팅 패스워드 */}
        <div className="row mb-3">
          <label className="col-sm-3 col-form-label">줌 미팅 패스워드</label>
          <div className="col-sm-4">
            <input type="password" value={zoomPassword} onChange={(e) => setZoomPassword(e.target.value)} className="form-control" />
          </div>
        </div>


        </div>
        <div className="modal-footer">
          <button type="button" className="btn btn-secondary" onClick={toggleModal}>닫기</button>
          <button type="button" className="btn btn-primary" onClick={handleSubmit}>저장</button>
        </div>
          </div>
        </div>
      </div>
    )}
  <table className="table">
          <thead>
            <tr>
              <th>강사번호</th>
              <th>이름</th>
              <th>이메일</th>
              <th>전화번호</th>
              <th>상세 정보</th>
              <th>강의 보기</th>
              <th>추가 수업 가능 시간</th>
              <th>수당 관리</th>
            </tr>
          </thead>
          <tbody>
            {classRoom.map((room, index) => (
              <tr key={index}>
                <td>{room.id}</td>
                <td>{room.name}</td>
                <td>{teacher.email}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <button className="btn btn-info">더 보기</button>
      </div>
    );
  };

export default ClassManagement;
