import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const ClassManagement = () => {
  const [classes, setClasses] = useState([]);
  const [selectedStatus, setSelectedStatus] = useState('ALL');
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    // 클래스 데이터 불러오기
    axios.get(`${API_BASE_URL}/classLoad`).then(response => {
      setClasses(response.data);
    });
  }, []);

  const filteredClasses = classes.filter(
    classItem => selectedStatus === 'ALL' ? true : classItem.status === selectedStatus
  );

  const handleCreateClass = () => {
    axios.get(`${API_BASE_URL}/createClass`).then(response => {
      
    });
  };
  const toggleModal = () => {
    setShowModal(!showModal);
  };

  return (
    <div className="container">
      <h1>클래스 관리</h1>
      <div>
        <button onClick={() => setSelectedStatus('ALL')}>전체</button>
        <button onClick={() => setSelectedStatus('RUNNING')}>운영 중</button>
        <button onClick={() => setSelectedStatus('ENDED')}>종료</button>
        <button className="btn btn-success" onClick={toggleModal}>강사 생성</button>
      </div>
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>클래스 생성</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* 클래스 생성 폼 */}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            취소
          </Button>
          <Button variant="primary" onClick={handleCreateClass}>
            생성
          </Button>
        </Modal.Footer>
      </Modal>
      <table>
        <thead>
          <tr>
            <th>클래스 이름</th>
            <th>상태</th>
          </tr>
        </thead>
        <tbody>
          {filteredClasses.map((classItem, index) => (
            <tr key={index}>
              <td>{classItem.name}</td>
              <td>{classItem.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ClassManagement;
