import React from 'react';
import './App.css';
import { BrowserRouter as Router, Route } from 'react-router-dom'; // BrowserRouter as Router와 Route만 임포트
import Header from './pages/componetes/header';
import Footer from './pages/componetes/footer';
import MainPage from './pages/componetes/main';
import SignupForm from './pages/componetes/signup'; // SignupForm 컴포넌트의 경로를 지정해야 합니다.
import axios from 'axios';

function App() {


  const [countryCodes, setCountryCodes] = useState([]);
  useEffect(() => {
      // 국가 코드를 불러오는 함수
      async function fetchCountryCodes() {
        try {
          const response = await axios.get('/api/countryCodes');
          setCountryCodes(response.data);
        } catch (error) {
          console.error("API 호출 중 에러 발생:", error);
        }
      }

      fetchCountryCodes();
    }, []);

  return (
    <div className="App">
      <Router>
        <Header />
        <Route exact path="/" component={MainPage} />
        <Route path="/signup" component={SignupForm} />
        <Footer />
      </Router>
    </div>
  );
}

export default App;
