import React, { useState, useEffect } from 'react';
import { Container, Form, Button, Row, Col, Modal } from 'react-bootstrap';
import '../style/signup.css';

import axios from 'axios';
import Select from 'react-select';



const App = () => {
const [searchTerm, setSearchTerm] = useState(""); 

const [countryCodes, setCountryCodes] = useState([]);

const [isCheckboxChecked, setIsCheckboxChecked] = useState(false);

const [showModal, setShowModal] = useState(false);  

const [selectedCountry, setSelectedCountry] = useState({
  value: '',
  label: '-- select --'
});

const [selectedOption, setSelectedOption] = useState({
  value: '82',  // 한국의 국가 코드
  label: '(82) Korea'
});

const options = countryCodes.map(codeInfo => ({
  value: codeInfo.callingCode,
  label: `(${codeInfo.callingCode}) ${codeInfo.name}`,
}));

const countryOptions = countryCodes.map(codeInfo => ({
  value: codeInfo.name, // 실제 값
  label: codeInfo.name // 보여지는 값
}));


  const openPopup = () => {
    if (!isCheckboxEnabled) {  // 체크가 안 된 상태에서만 모달 띄우기
      setShowModal(true);
    }
  };

const handleCheckboxClick = () => {
  if (!isCheckboxChecked && !isCheckboxEnabled) {
    setShowModal(true);  // 체크되기 전, 활성화되기 전이면 모달을 띄운다.
  }
  setIsCheckboxChecked(!isCheckboxChecked);  // 체크박스 상태를 반전시킨다.
};

const handleAgree = () => {
  setIsCheckboxChecked(true);  // 체크박스를 활성화시킨다.
  setShowModal(false);  // 모달을 닫는다.
};

const SignupPopup = ({ show, onHide, onAgree }) => (
  <Modal show={show} onHide={onHide}>
    <Modal.Header closeButton>
      <Modal.Title>약관 동의</Modal.Title>
    </Modal.Header>
    <Modal.Body>약관 </Modal.Body>
    <Modal.Footer>
      <Button variant="secondary" onClick={onHide}>
        취소
      </Button>
      <Button variant="primary" onClick={onAgree}>
        동의
      </Button>
    </Modal.Footer>
  </Modal>
);

useEffect(() => {
  // 국가 코드를 불러오는 함수
  async function fetchCountryCodes() {
    try {
      const response = await axios.get('http://localhost:4020/api/countryCodes');
      const countryNames = response.data.map(item => item.name);
      setCountryCodes(response.data);
    } catch (error) {
      console.error("API 호출 중 에러 발생:", error);
    }
  }

  window.addEventListener('message', messageListener);
  
  fetchCountryCodes();

  return () => {
    // 컴포넌트 unmount 될 때 이벤트 리스너 해제
    window.removeEventListener('message', messageListener);
  };
}, []);

const messageListener = (event) => {
  if (event.data === 'agreed') {
    setIsCheckboxEnabled(true);
  }
};
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    birthYear: '',
    birthMonth: '',
    birthDay: '',
    country_code: '',
    phone_number: ''
  });

  
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  // 폼을 제출하는 함수
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form data submitted', formData);
  };
  

  return (
    <Container className="container">
      <h1>Application Form</h1><br/>
      <h2>for K-talk Live regular paid Korean lessons</h2>
      <h4>·Please complete and submit this form so that teachers can contact you for the next step.
          <br/>
          ·If you'd like to apply for our Free Hangeul Lessons, please exit this page and visit our website at
          <br/>    
          http://ktalklive.com
          or our FB page at 
          https://www.facebook.com/ktalklive<br/><br/>
          Thank you very much!<br/></h4>
  
    <Form onSubmit={handleSubmit}>
    <Form.Group as={Row} className="form-group">
    <Form.Label column sm="2">
      Name in full (First/Last):
    </Form.Label>
    <Col sm="5">
      <Form.Control
        type="text"
        placeholder="First Name"
        name="firstName"
        value={formData.firstName}
        onChange={handleChange}
        required
      />
    </Col>
    <Col sm="5">
      <Form.Control
        type="text"
        placeholder="Last Name"
        name="lastName"
        value={formData.lastName}
        onChange={handleChange}
        required
      />
    </Col>
  </Form.Group>

  <Form.Group as={Row} className="form-group">
    <Form.Label column sm="2">
      Email Address
    </Form.Label>
    <Col sm="10">
      <Form.Control
        type="email"
        placeholder="Address@gmail.com"
        name="email"
        value={formData.email}
        onChange={handleChange}
        required
      />
    </Col>
  </Form.Group>

  <Form.Group as={Row} className="form-group">
  <Form.Label column sm="2">
    Date of Birth:
  </Form.Label>
  <Col sm="10">
    <div style={{ display: "flex" }}>
      <Form.Control 
        type="text" 
        list="birthYears" 
        name="birthYear" 
        className="form-control" 
        placeholder="Year" 
        required 
        style={{width: "100px", marginRight: "10px"}}
      />
      <datalist id="birthYears">
        {[...Array(61).keys()].map(i => i + 1950).map(year => (
          <option key={year} value={year} />
        ))}
      </datalist>
      
      <Form.Control 
        type="text" 
        list="birthMonths" 
        name="birthMonth" 
        className="form-control" 
        placeholder="Month" 
        required 
        style={{width: "100px", marginRight: "10px"}}
      />
      <datalist id="birthMonths">
        {[...Array(12).keys()].map(i => i + 1).map(Month => (
          <option key={Month} value={Month} />
        ))}
      </datalist>
      
      <Form.Control 
        type="text" 
        list="birthDays" 
        name="birthDay" 
        className="form-control" 
        placeholder="Day" 
        required 
        style={{width: "100px"}}
      />
      {/* 여기에 datalist birthDays 추가 */}
      <datalist id="birthDays">
        {[...Array(31).keys()].map(i => i + 1).map(Day => (
          <option key={Day} value={Day} />
        ))}
        </datalist>
    </div>
  </Col>
</Form.Group>

<Form.Group as={Row} className="form-group">
  <Form.Label column sm="2">
    Nationality
  </Form.Label>
  <Col sm="10">
    <Select 
      options={countryOptions} 
      isSearchable={true} 
      value={selectedCountry}
      onChange={(option) => setSelectedCountry(option)}
    />
  </Col>
</Form.Group>

<Form.Group as={Row} className="form-group">
    <Form.Label column sm="2">
      Language you use:
    </Form.Label>
    <Col sm="10">
      <Form.Control 
        as="input" 
        list="countries" 
        name="country" 
        placeholder="Up to 3 languages" 
        required />
    </Col>
  </Form.Group>

  <Form.Group as={Row} className="form-group">
      <Form.Label column sm="2">
        Phone number
      </Form.Label>
      <Col sm="10">
        <div className="phone-container ">
          <Select 
            options={options} 
            isSearchable={true} 
            value={selectedOption}
            onChange={(option) => setSelectedOption(option)}
            className='mb-2 phone_code'
          />
          <Form.Control
            type="text"
            name="phone_number"
            className="form-control mb-3"
            required
          />
        </div>
      </Col>
    </Form.Group>
  
    <Form.Group as={Row} className="form-group">
    <h2>Please choose your available class hours in your local time.</h2>
    <Form.Label>Please indicate your country of residence with states and city:</Form.Label>
    <Form.Control type="text" name="currentCity" placeholder="country / state / city" />
  </Form.Group>
  
  <div className="d-flex align-items-center">
  <Form.Check 
      type="checkbox" 
      label="I agree to the terms and conditions" 
      id="termsCheckbox"
      onClick={handleCheckboxClick}
    />
    </div>


    <SignupPopup show={showModal} onHide={() => setShowModal(false)} onAgree={handleAgree} />


  <Button type="submit">Submit application</Button>




</Form>
  </Container>
  );
};

export default App;
