import React, { useState } from 'react';


const countryCodes = [
    { callingCode: '+1', name: 'United States' },
    { callingCode: '+82', name: 'South Korea' },
    // 여기에 더 추가 가능
  ];
  
  const timezones = [
    ['UTC-11:00', 'UTC-10:00', 'UTC-09:30'],
    ['UTC-09:00', 'UTC-08:30', 'UTC-08:00'],
    // 여기에 더 추가 가능
  ];
const SignupForm = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    birthYear: '',
    birthMonth: '',
    birthDay: '',
    country_code: '',
    phone_number: '',
    Nationality: '',
    country: '',
    currentCity: '',
    timezones: [],
    termsCheckbox: false,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // 서버로 데이터 전송하는 로직
  };
  return (
    <form action="/signup" method="post" className="signup-form" onSubmit={handleSubmit}>
   
      <div className="form-group">
        <label htmlFor="name-group">Name in full (First/Last):</label>
        <div id="name-group" className="d-flex">
          <input type="text" id="firstName" name="firstName" className="form-control" placeholder="First Name" required style={{marginRight: "20px"}} onChange={handleChange} />
          <input type="text" id="lastName" name="lastName" className="form-control" placeholder="Last Name" required onChange={handleChange} />
        </div>
      </div>
      {}
           <div className="form-group">
           <label htmlFor="phone_number">Phone number</label>
           <div className="phone-container">
             <select name="country_code" id="country_code" className="form-control" onChange={handleChange}>
               {/* ... 여기에 country code option들 */}
             </select>
             <input
               type="text"
               id="phone_number"
               name="phone_number"
               className="form-control"
               required
               placeholder=""
               onChange={handleChange}
             />
           </div>
         </div>
      <button type="submit" id="submitButton">Submit application</button>
    </form>
  );
};

export default SignupForm;
